      out.write("    	e.preventDefault();\r\n");
      out.write("	});\r\n");
      out.write("\r\n");
      out.write("});\r\n");
      out.write("\r\n");
      out.write("var uploadCountLimit = 5; // 업로드 가능한 파일 갯수\r\n");
      out.write("var fileCount = 0; // 파일 현재 필드 숫자 totalCount랑 비교값\r\n");
      out.write("var fileNum = 0; // 파일 고유넘버\r\n");
      out.write("var content_files = new Array(); // 첨부파일 배열\r\n");
      out.write("var rowCount=0;\r\n");
      out.write("\r\n");
      out.write("function fileCheck(files) {\r\n");
      out.write("\r\n");
      out.write("	//var files = e.target.files;\r\n");
      out.write("	var filesArr = Array.prototype.slice.call(files);\r\n");
      out.write("	\r\n");
      out.write("    // 파일 개수 확인 및 제한\r\n");
      out.write("    if (fileCount + filesArr.length > uploadCountLimit) {\r\n");
      out.write("      	alert('파일은 최대 '+uploadCountLimit+'개까지 업로드 할 수 있습니다.');\r\n");
      out.write("      	return;\r\n");
      out.write("    } else {\r\n");
      out.write("    	 fileCount = fileCount + filesArr.length;\r\n");
      out.write("    }\r\n");
      out.write("\r\n");
      out.write("    var i =0;\r\n");
      out.write("	filesArr.forEach(function (f) {\r\n");
