      out.write("	\r\n");
      out.write("	$(\"#input_file\").val(\"\");	\r\n");
      out.write("\r\n");
      out.write("}	\r\n");
      out.write("\r\n");
      out.write("function fileDelete(fileNum){\r\n");
      out.write("    var no = fileNum.replace(/[^0-9]/g, \"\");\r\n");
      out.write("    content_files[no].is_delete = true;\r\n");
      out.write("	$('#' + fileNum).remove();\r\n");
