      out.write("}  \r\n");
      out.write("\r\n");
      out.write("var progressBarWidth = 200;\r\n");
      out.write("\r\n");
      out.write("function setProgress(progress){       \r\n");
      out.write("    //var ActiveprogressBarWidth =progress*progressBarWidth/ 100;  \r\n");
      out.write("    $(\".progressBar div\").animate({ width: progress }, 10).html(progress + \"% \");\r\n");
      out.write("}\r\n");
      out.write("\r\n");
      out.write("function sendFileToServer()\r\n");
      out.write("{\r\n");
      out.write("	var form = $(\"#formdata\")[0];\r\n");
      out.write(" 	var formData = new FormData(form);\r\n");
      out.write("	for (var x = 0; x < content_files.length; x++) {\r\n");
      out.write("			if(!content_files[x].is_delete) { \r\n");
      out.write("						\r\n");
      out.write("				formData.append(\"SendToFileList\", content_files[x]);\r\n");
      out.write("			}\r\n");
      out.write("	}\r\n");
      out.write("\r\n");
      out.write("	var uploadURL = \"/Intranet/fileUpload?kinds=R\"; \r\n");
      out.write("     \r\n");
      out.write("	$.ajax({\r\n");
      out.write("        url: uploadURL,\r\n");
      out.write("        type: \"POST\",\r\n");
